import React, { useEffect, useState } from 'react'
import Main from './components/Main'
import { useMeQuery } from './generated/graphql'
import UserContext from './userContext'

require('halfmoon/css/halfmoon.min.css')

const halfmoon = require('halfmoon')

const App: React.FC = () => {
  const [user, setUser] = useState<any>()
  const { data, error, loading } = useMeQuery()

  const logout = () => {
    localStorage.removeItem('token')
    window.location.reload()
  }

  useEffect(() => {
    if (data) {
      if (data && data.me) {
        console.log(data.me)
        setUser(data.me)
      }
    }
    if (error) {
      console.log(error)
    }
    if (loading) {
      console.log(loading)
    }
  }, [data, error, loading])

  useEffect(() => {
    document.body.classList.add('with-custom-webkit-scrollbars')
    document.body.classList.add('with-custom-css-scrollbars')

    const darkModePref = localStorage.getItem('prefer-dark-mode')

    if (darkModePref === 'true') {
      halfmoon.toggleDarkMode()
    }

    halfmoon.onDOMContentLoaded()
  }, [])

  return (
    // <div
    //   id="pageWrapper"
    //   className="page-wrapper with-navbar with-sidebar" //with-sidebar
    //   data-sidebar-type="overlayed-all"
    // >
    //   <div className="sidebar-overlay" onClick={toggleSidebar}></div>
    //   <div className="sidebar border-0">
    //     <UserCard />
    //   </div>

    //   <Helmet>
    //     <meta charSet="utf-8" />
    //     <title>Avarts</title>
    //   </Helmet>

    //   <Header />

    //   {user ? (
    //     <UserContext.Provider
    //       value={{
    //         user: {
    //           id: user.id,
    //           name: user.name,
    //           img: user.img,
    //           // TODO: This is ugly. Find a better way to handle when a User has no activities
    //           latestActivity:
    //             user.latestActivity && user.latestActivity.title
    //               ? user.latestActivity
    //               : { title: '', createdAt: '' },
    //           activityCount: user.activityCount,
    //         },
    //         logout: logout,
    //       }}
    //     >
    //       <Main />
    //     </UserContext.Provider>
    //   ) : (
    //     <SignUp />
    //   )}
    // </div>
    <UserContext.Provider
      value={{
        user: {
          id: user ? user.id : '0',
          name: user ? user.name : '',
          img: user ? user.img : '',
          // TODO: This is ugly. Find a better way to handle when a User has no activities
          latestActivity:
            user & user.latestActivity && user.latestActivity.title
              ? user.latestActivity
              : { title: '', createdAt: '' },
          activityCount: user ? user.activityCount : 0,
        },
        logout: logout,
      }}
    >
      <Main />
    </UserContext.Provider>
  )
}

export default App
