import { UNDEFINED } from '../constants';

export default () => {
  const d =
    typeof performance === UNDEFINED ? Date.now() : performance.now() * 1000;

  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
    const r = (Math.random() * 16 + d) % 16 | 0;

    return (c == 'x' ? r : (r & 0x3) | 0x8).toString(16);
  });
};
import isUndefined from './isUndefined';
import isArray from './isArray';
import unique from './unique';

const removeAt = <T>(data: T[], index: number): T[] => [
  ...data.slice(0, index),
  ...data.slice(index + 1),
];

function removeAtIndexes<T>(data: T[], index: number[]): T[] {
  let k = -1;

  while (++k < data.length) {
    if (index.indexOf(k) >= 0) {
      delete data[k];
    }
  }

  return unique(data);
}

export default <T>(data: T[], index?: number | number[]): T[] =>
  isUndefined(index)
    ? []
    : isArray(index)
    ? removeAtIndexes(data, index)
    : removeAt(data, index);
import isUndefined from './isUndefined';
import isArray from './isArray';

export default <T>(data: T[], from: number, to: number): (T | undefined)[] => {
  if (isArray(data)) {
    if (isUndefined(data[to])) {
      data[to] = undefined as any;
    }
    data.splice(to, 0, data.splice(from, 1)[0]);
    return data;
  }

  return [];
};
export default <T>(data: T[], indexA: number, indexB: number): void => {
  const temp = [data[indexB], data[indexA]];
  data[indexA] = temp[0];
  data[indexB] = temp[1];
};
import isArray from './isArray';

export default function prepend<T>(data: T[]): (T | undefined)[];
export default function prepend<T>(data: T[], value: T | T[]): T[];
export default function prepend<T>(
  data: T[],
  value?: T | T[],
): (T | undefined)[] {
  return [...(isArray(value) ? value : [value || undefined]), ...data];
}
import isArray from './isArray';

export default function insert<T>(data: T[], index: number): (T | undefined)[];
export default function insert<T>(
  data: T[],
  index: number,
  value: T | T[],
): T[];
export default function insert<T>(
  data: T[],
  index: number,
  value?: T | T[],
): (T | undefined)[] {
  return [
    ...data.slice(0, index),
    ...(isArray(value) ? value : [value || undefined]),
    ...data.slice(index),
  ];
}
