import React, { useEffect, useState } from 'react'
import Main from './components/Main'
import { useMeQuery, User } from './generated/graphql'
import UserContext from './userContext'

require('halfmoon/css/halfmoon.min.css')

const halfmoon = require('halfmoon')

type UserContextType = Pick<User, 'id' | 'name' | 'email'>

const App: React.FC = () => {
  const [user, setUser] = useState<UserContextType>()
  const { data, error, loading } = useMeQuery()

  const logout = () => {
    localStorage.removeItem('token')
    window.location.reload()
  }

  useEffect(() => {
    if (data) {
      if (data && data.me) {
        setUser(data.me)
      }
    }
    if (error) {
      console.log(error)
    }
    if (loading) {
      console.log(loading)
    }
  }, [data, error, loading])

  useEffect(() => {
    document.body.classList.add('with-custom-webkit-scrollbars')
    document.body.classList.add('with-custom-css-scrollbars')

    const darkModePref = localStorage.getItem('prefer-dark-mode')

    if (darkModePref === 'true') {
      halfmoon.toggleDarkMode()
    }

    halfmoon.onDOMContentLoaded()
  }, [])

  return (
    <div>
      {user ? (
        <UserContext.Provider
          value={{
            user: {
              id: user.id,
              name: user.name,
            },
            logout: logout,
          }}
        >
          <Main />
        </UserContext.Provider>
      ) : (
        <h2>hmm</h2>
      )}
    </div>
  )
}

export default App
